import React, { useState, useEffect } from "react";
function Input1(props) {
    const [inputs, setInputs] = useState({
        name: "",
        nickname: "",
      });
      const { name, nickname } = inputs;
      const onChange = (e) => {
        const { name, value } = e.target;

        setInputs({
          ...inputs,
          [name]: value,
        });
      };
      const onReset = () => {
        setInputs({
          name: "",
          nickname: "",
        });
      };
      return (
        <div>
          <input 
              name="name" 
            placeholder="이름" 
            onChange={onChange} 
            value={name} 
          />
          <input
            name="nickname"
            placeholder="닉네임"
            onChange={onChange}
            value={nickname}
          />
          <div>
            <b>값: </b>
            {name}
          </div>
          <div>
            <b>값: </b>
            {nickname}
          </div>
        </div>
      );
}

export default Input1;
