import React from "react";

const styles ={
    image: {
        margin: auto,
        width: 50,
        height: 50,
        borderRadius: 25,
    },
}

function Card(props){
    const {title, backgroundColor, children} = props;

    return(
        <div
            style={{
                margin:8,
                padding:8,
                borderRadius:8,
                boxShadow: "0px 0px 4px grey",
                backgroundColor: backgroundColor || "white",
            }}>
                {title && <h1>{title}</h1>}
                {children}
                <img
                    src={props.img}
                    style={styles.image}
                />
        </div>
    );
}

export default Card;
